import {
  IonButtons, 
  IonContent, 
  IonHeader, 
  IonMenuButton, 
  IonPage, 
  IonTitle, 
  IonToolbar,
  IonButton,
  IonIcon,
  IonImg,
  IonGrid,
  IonRow,
  IonSearchbar,
  IonCol
} from '@ionic/react';
import { cameraOutline } from 'ionicons/icons';
import { useParams } from 'react-router';
import React, {useState, useEffect} from 'react';

import { Device } from '@capacitor/device';
import { Filesystem, Directory, /*Encoding*/ } from '@capacitor/filesystem';
import { Geolocation } from '@capacitor/geolocation';
import { Haptics } from '@capacitor/haptics';
import { LocalNotifications } from '@capacitor/local-notifications';
import { Share } from '@capacitor/share';
import { StatusBar, Style } from '@capacitor/status-bar';
import { Toast } from '@capacitor/toast';
//import { AppLauncher } from '@capacitor/app-launcher';
import { Camera, CameraResultType } from '@capacitor/camera';
import { BleClient } from '@capacitor-community/bluetooth-le';
//import { Contacts } from '@capacitor-community/contacts'
import { Browser } from '@capacitor/browser';

//import ExploreContainer from '../components/ExploreContainer';
import './Page.css';
import Tabs from './Tabs';

//import { AndroidPermissions } from '@awesome-cordova-plugins/android-permissions/ngx';

const Page: React.FC = () => {


  const logCurrentPosition =  async () => {
   let coords = await Geolocation.getCurrentPosition();
    alert(JSON.stringify(coords));

  };

  const logDeviceInfo = async () => {
    let info = await Device.getInfo();
    alert(JSON.stringify(info));
};
  /*
  const _checkPermission = () => {
    AndroidPermissions.checkPermission(AndroidPermissions.PERMISSION.CAMERA,
    (result: any) => alert(result),
    (error: any) => alert(error)
    );
  }
  */
  const doVibrate = async () => {
    await Haptics.vibrate();
  };

  const doShare = async () => {
    let canShare = await Share.canShare();
    !canShare ? alert('Not suport share functionality') : 
     await Share.share({
       title: 'Just a secret',
       text: 'A secret you must know but it is a secret',
       url: 'http://ionicframework.com/',
       dialogTitle: 'Share with buddies',
    });
  };
  /*
  const sendLocalNotification = async () => {
    await LocalNotifications.schedule({
      notifications: [
        {
           title: 'Just a simple local notification',
           body: 'A body',
           summaryText: 'Lorem ipsum dolor sit atmet',
           id: 1
        }
      ]
     });
  };
  */
  const showToast = async () => {
    try{
     // require PWA element loader
     await Toast.show({
       text: 'Hello, I will be hidden soon!',
       duration: 'short',
       position: 'bottom'
     });
    }catch (e){
     alert(e);
     return;
    }
  };
  /*
  const checkCanOpenUrl = async () => {
    const { value } = await AppLauncher.canOpenUrl({ url: 'com.duongvanphi.myapp' });
    alert('Can open url: '+value);
  };

  const openPortfolioPage = async () => {
    await AppLauncher.openUrl({ url: 'com.duongvanphi.myapp' });
  };
  */
  const [imgSource, setImgSource] = useState('');

  const takePicture = async () => {
    const image = await Camera.getPhoto({
      quality: 90,
      allowEditing: true,
      resultType: CameraResultType.Uri
    });
    //alert(JSON.stringify(image));
    let uri = image.webPath || '';
    setImgSource(uri);
  }

  /*
  const getContacts = async () => {
    try{
     let contacts = await Contacts.getContacts();
     alert(JSON.stringify(contacts));
    }catch(e){
      alert(e);
      return;
    }
  }
 */

  const bluetoothStuff = async () => {
    try{
      await BleClient.initialize();
      let isEnabled = await BleClient.isEnabled();
      let results = isEnabled && await BleClient.requestDevice({
        services: ['0000180d-0000-1000-8000-00805f9b34fb'],
        allowDuplicates: true
      });
      alert(JSON.stringify(results));
    }catch(e) {
      alert(e);
    }
  };

  const openBrowser = async () => {
     try{
        await Browser.open({ url: 'http://capacitorjs.com/' });
      }catch(e){
        alert(e);
      }
  };

  useEffect(() =>{
    null && (async () => {
      try{
        await StatusBar.setStyle({style: Style.Light});
        alert('set');
      }catch (e){
        alert(e);
        return;
        }
    })();


    null && (async () =>{
      try{
        let data = await Filesystem.readdir({
          path: '',
          directory: Directory.ExternalStorage
        });
        alert(JSON.stringify(data));
       }catch(e){
         alert(e);
         return;
      }
    })();

    null &&  (async () => {
      try{
        let status = await LocalNotifications.requestPermissions();

      alert(JSON.stringify(status));
      }catch (e) {
        alert(e);
        return;
      }
    })();

    (async () => {
      console.log("logging");
    })();

  }, []);

  const { name } = useParams<{ name: string; }>();


  return (
    <IonPage >
      <IonHeader translucent={true}>
        <IonToolbar>
          <IonButtons slot="start">
            <IonMenuButton />
          </IonButtons>
          <IonTitle>{name}</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent fullscreen={true}>
        <IonHeader collapse="condense">
          <IonToolbar>

            <IonTitle size="large">{name}</IonTitle>
          </IonToolbar>
          <IonSearchbar/>
        </IonHeader>
        <IonGrid className="ion-padding">
          <IonRow>
            <IonCol>
             <IonButton onClick={() => logDeviceInfo()}>Device Info</IonButton>
             <IonButton onClick={() => logCurrentPosition()}>Current Position</IonButton>
             <IonButton onClick={() => doVibrate()}>Vibrate</IonButton>
             <IonButton onClick={() => doShare()}>Share</IonButton>
             <IonButton onClick={() => showToast()}>Show A Toast</IonButton>
             <IonButton onClick={() => bluetoothStuff()}>Bluetooth</IonButton>
            <IonButton onClick={() => openBrowser()}>Open Browser</IonButton>
          </IonCol>
          <IonCol className="ion-text-center"> 
            <IonButton fill="outline" onClick={() => takePicture()}>Take A Picture
              <IonIcon slot="start" icon={cameraOutline}/>
            </IonButton>
          </IonCol>

          {imgSource && <IonImg src={imgSource}/>}
         {/* <IonButton onClick={() => sendLocalNotification()}>Send Local Notification</IonButton>
         */}
           </IonRow>
         </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Page;
